import React, { useState } from "react";
import { BrowserRoute, Switch, route } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import Djam from "./components/api/Vue";
import "./App.css";
import "./components/request/getrequest";

function App() {

  const [count, setCount] = useState(0);

  const display = (i) => {
    setCount(i)
  }

  const [data, setData] = React.useState(null);

  React.useEffect(() => {
    fetch("/api")
      .then((res) => res.json())
      .then((data) => setData(data.message));
  }, []);


  return (

    <div>
      <div className={count === 0 ? "display" : "none"}>
        <Djam />
        <button onClick={() => display(2)} className="btn1"  >
          search
        </button>
      </div>
      <div className={count === 2 ? "display" : "none"} id="page2" >
        <p>FTF ADD YOUR REQUEST</p>
        <button onClick={() => display(3)} className="btn">
          GET
        </button>
        <button onClick={() => display(4)} className="btn">
          POST
        </button>
        <button onClick={() => display(3)} className="btn">
          DELETE
        </button>
        <button onClick={() => display(3)} className="btn">
          PUT
        </button>
        <div className="form">
          <form>
            <input type="id" placeholder="identifiant du joueur" className="input1" ></input>
            <input type="url" placeholder="url du joueur" className="input2"></input>
            <button onClick={() => display(3)} className="btn"> GET</button>
          </form>
          <BrowserRouter>
            <div className='App'>
              <ToastContainer>
                <switch>
                  <Route react path='/getrequest' components={getrequest} />
                </switch>
              </ToastContainer>
            </div>
          </BrowserRouter>
        </div>
        <p>{!data ? "Loading..." : data}</p>
      </div>
      <div className={count === 4 ? "display" : "none"}>
        <p>bienvenue</p>
      </div>
    </div>

  );
}

export default App;
